mapper.select:
SELECT INTERVIEW_ID as interviewid ,COMPANY_ID as companyid ,PERSON_ID as personid ,CASE_NAME as casename ,WORK_PLACE as workplace ,INTERVIEW_PLACE as interviewplace ,INTERVIEW_DATE as interviewdate ,START_TIME as starttime ,END_TIME as endtime ,CONCAT(INTERVIEW_DATE,' ',START_TIME,'～',END_TIME) AS INTERVIEW_DATETIME ,WORK_CONTENTS as workcontents ,CASE INTERVIEW_RESULT WHEN '1' THEN '招待された' WHEN '2' THEN '面接を受けた' WHEN '3' THEN '面接を断った' WHEN '4' THEN '結果まち' WHEN '5' THEN '合格' WHEN '6' THEN '不合格' WHEN '7' THEN '面接がキャンセルされた' WHEN '8' THEN '面接参加をキャンセルした' WHEN '9' THEN '合格を辞退した' ELSE 'なし' END as interviewresult ,CREATE_USER ,CREATE_DATE_TIME ,UPDATE_USER ,UPDATE_DATE_TIME ,CREATED_BY ,UPDATED_BY FROM T_B_INTERVIEW WHERE 1 = 1
AND PERSON_ID = #{personId}
AND CASE_NAME like concat('%',#{casename},'%')
AND INTERVIEW_DATE like concat('%',#{casetime},'%')
AND INTERVIEW_PLACE like concat('%',#{caseplace},'%')
AND INTERVIEW_RESULT =#{result}
ORDER BY UPDATE_DATE_TIME DESC LIMIT #{mylimit} OFFSET #{myoffset}
