mapper.select:
SELECT INTERVIEW_ID as interviewid ,COMPANY_ID as companyid ,PERSON_ID as personid ,CASE_NAME as casename ,WORK_PLACE as workplace ,INTERVIEW_PLACE as interviewplace ,INTERVIEW_DATE as interviewdate ,START_TIME as starttime ,END_TIME as endtime ,CONCAT(INTERVIEW_DATE,' ',START_TIME,'ï½ž',END_TIME) AS INTERVIEW_DATETIME ,WORK_CONTENTS as workcontents ,INTERVIEW_RESULT as interviewresult ,CREATE_USER ,CREATE_DATE_TIME ,UPDATE_USER ,UPDATE_DATE_TIME ,CREATED_BY ,UPDATED_BY FROM T_B_INTERVIEW WHERE 1 = 1
AND PERSON_ID = #{personId}
ORDER BY UPDATE_DATE_TIME DESC LIMIT #{mylimit} OFFSET #{myoffset}
